<?php	defined( '_JEXEC' ) or die( 'Restricted access' );	if(!defined('DS')) define('DS', DIRECTORY_SEPARATOR);	$params = JComponentHelper::getParams('com_fgbusupport' );	if (!($params->get('SupportProtocol') && $params->get('SupportURL') && $params->get('SupportID')))		return JError::raiseWarning(0, 'Система не настроена обратитесь к администратору');	JLoader::register('FgbusupportHelperQuery', JPATH_COMPONENT_ADMINISTRATOR.DS.'helpers'.DS.'query.php');	if($controller = JRequest::getVar('controller')) {		require_once(JPATH_COMPONENT . DS . 'controllers' . DS . $controller . '.php');		$classname  = 'FgbusupportController'.$controller;		$controller = new $classname;	}	else		$controller = JControllerLegacy::getInstance( 'fgbusupport' );	$controller->execute( JFactory::getApplication()->input->get( 'task' ) );	$controller->redirect();/**/?>