<?php	defined('_JEXEC') or die('Restricted Access');	JHtml::addIncludePath(JPATH_COMPONENT . '/helpers/html');	$params = JComponentHelper::getParams('com_fgbusupport' );	JHtml::_('behavior.formvalidation');	//var_export($this->item);?><script type="text/javascript">  Joomla.submitbutton = function(task)	{		if (task == 'Issue.cancel' || document.formvalidator.isValid(document.id('adminForm'))) {			Joomla.submitform(task, document.getElementById('adminForm'));		}	}</script>	<a class="btn btn-primary" href="<?php Echo JRoute::_('index.php?option=com_fgbusupport&view=issues')?>"><span class="icon-list"></span>Назад</a>	<?php if (!empty($this->item)):?>		<?php echo JHtml::_('icon.create'); ?>		<div id="j-main-container">			<div class="panel panel-info">				<div class="panel-heading">					<h3 class="panel-title">№ <?php Echo $this->item->supp_num?> - <?php Echo $this->item->caption?> </h3>				</div>				<div class="panel-body">					<?php Echo $this->item->topic?>				</div>			</div>			<?php 				if (is_array($this->item->support -> clarification))				foreach($this->item->support -> clarification as $support): ?>				<div data-toggle="tooltip" data-placement="left" 					style="margin-<?php Echo $support->action=='issue_action_clarification'?'right':'left'?>: 5%;" 					class="alert alert-<?php Echo $support->action=='issue_action_clarification'?'warning':'info '?>">					<strong><?php Echo $support-> date?></strong> <?php Echo $support-> message?>				</div>			<?php endforeach; ?>			<?php if (!empty($this->item->support->reply)):?>				<div data-toggle="tooltip" data-placement="left" title="<?php Echo $this->item->support->date_end?>"   style="" class="alert alert-success">					<strong><?php Echo $this->item->support->reply?></strong>				</div>			<?php endif	?>			<?php if (($this->item->support->status_id == 'issue_tbd') && ($params->get('EnableRequest'))):?>				<form action="" method="post" name="adminForm" id="adminForm">					<div style="margin-left: 5%;" class="alert alert-info">						<div class="input-group">							<?php echo $this->form->getField('message')->input; ?>							<span class="input-group-addon">								<button type="button" class="btn btn-primary" onclick="Joomla.submitbutton('answer.clarified')"><span class="icon-play"></span></button>							</span>						</div><!-- /input-group -->					</div>					<div style="display:none">						<input type="hidden" name="task" value="" />						<input type="hidden" name="id" value="<?php Echo $this->item->id?>" />						<input type="hidden" name="option" value="com_fgbusupport" />						<input type="hidden" name="view" value="answer" />						<?php echo JHtml::_('form.token'); ?>					</div>				</form>			<?php endif?>		</div>	<?php endif	?>		